pragma solidity ^0.4.24;
import '../installed_contracts/oraclize-api/contracts/usingOraclize.sol';

contract BtcPriceGetter is usingOraclize {

    uint public bitcoinPrice;

    event newOraclizeQuery(string description);
    event newOraclizeQueryFailed(string description);
    event newKrakenPriceTicker(bytes32 myid,uint price);

    constructor() public {
        updatePrice(0);
    }

    function __callback(bytes32 myid, string result, bytes proof) public {
        require(msg.sender == oraclize_cbAddress());
        bitcoinPrice = parseInt(result, 2); // save it in storage as $ cents
        emit newKrakenPriceTicker(myid, bitcoinPrice);
    }

    function updatePrice(uint delay) public payable {
        if (oraclize_getPrice("URL") > address(this).balance) {
            emit newOraclizeQueryFailed("Oraclize query was NOT sent, please add some ETH to cover for the query fee");
        } else {
            emit newOraclizeQuery("Oraclize query was sent, standing by for the answer..");
            oraclize_query(delay, "URL", "json(https://api.kraken.com/0/public/Ticker?pair=XBTUSD).result.XXBTZUSD.c.0");
        }
    }
}
